    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>module</span> <span class="token attr-name">name</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>JavadocVariable<span class="token punctuation">"</span></span><span class="token punctuation">&gt;</span></span>
